mapper.select:
SELECT CONTROL_NUM, INDICATORS_NUM, INDICATORS_NAME, IS_BEST, IS_AGENCY, IS_AHA, IS_LINC, IS_TYPE3, ELEMENTS, COL_METHOD, EVAL_METHOD, EVAL_STANDARD, DIVISION_NAME, TARGET_VAL, FORMULA, IS_NEGATIVE, IS_USE, USER_ID, USER_NAME, ACTION_CODE, (SELECT MAX(RECORD_DATE) FROM RECORD_TB WHERE INDICATORS_NUM = ind.INDICATORS_NUM GROUP BY INDICATORS_NUM) RECORD_DATE FROM INDICATORS_TB ind WHERE (INDICATORS_NUM, RECORD_DATE) IN ( SELECT INDICATORS_NUM, MAX(RECORD_DATE) FROM INDICATORS_TB GROUP BY INDICATORS_NUM )
AND IS_BEST = #{IS_BEST}
AND IS_AGENCY = #{IS_AGENCY}
AND IS_AHA = #{IS_AHA}
AND IS_LINC = #{IS_LINC}
AND IS_TYPE3 = #{IS_TYPE3}
AND DIVISION_NAME = #{DIVISION_NAME}
AND INDICATORS_NAME like '%'||#{INDICATORS_NAME}||'%'
AND IS_USE = 0 ORDER BY INDICATORS_NUM
